import React, { Component } from 'react'
import { motion } from 'framer-motion'
import Footer from './Footer'

const linkedInUrl = "https://www.linkedin.com/in/andrew-zajac-017672199"
const gitHubUrl = "https://github.com/ajzajac"

export class Contact extends Component {

    sendEmail = () => {
      window.location.href='mailto:ajzajac@icloud.com'
    }

    getLinkedIn = () => {
        var win = window.open(linkedInUrl, '_blank');
        win.focus()
    }

    goToGithub = () => {
        var win = window.open(gitHubUrl, '_blank');
        win.focus()
    }

    render() {
        const container = {
            hidden: { opacity: 0 },
            show: {
              opacity: 1,
              transition: {
                  staggerChildren: 2,
                  ease: "easeIn", 
                  duration: 2.5,

              }
            }
          }

          const item = {
            hidden: { opacity: 0 },
            show: { opacity: 1 }
          }

        return (
            <div className="contactMePage">
                <h2>Contact Me</h2>
                    <div className="emailLink">
                        <motion.ul>
                            <motion.li></motion.li><img src="linkedin.png" height="110px" width="110px" onClick={this.getLinkedIn} />
                            <img src="githubLogo.jpg" height="125px" width="125px" onClick={this.goToGithub}/>
                            <img src="email.png" height="150px" width="135px"  onClick={this.sendEmail} />
                        </motion.ul>
                    </div> 
                <Footer></Footer>
            </div>
        )
    }
}

export default Contact
